import React from "react";
import { useState } from "react";
import emailjs from 'emailjs-com';

function Contact(){

    const ContactForm = () => {
        const[formData, setFormData] = useState({
            name: '',
            email: '',
            message: ''
        });

        const handleChange = (e) => {
            const{name, value} = e.target;
            setFormData({
                ...formData,
                [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
    
        emailjs.send('service_dhcw70g', 'template_w6tmrpd', formData, 'YOUR_USER_ID')
          .then((response) => {
            console.log('SUCCESS!', response.status, response.text);
            alert('Message sent successfully!');
          }, (error) => {
            console.log('FAILED...', error);
            alert('Failed to send the message. Please try again.');
          });
      };

    return(
        <div>
            <h1 id = "cont_title">Send Me A Message</h1>
            <form id = "contact_form_container" onSubmit={handleSubmit}>
                <label >Name: </label>
                <input type = "text" name = "to_name" value={formData.name} onChange={handleChange} required></input>

                <label >Email: </label>
                <input type = "email"  name="from_email" value={formData.email} onChange={handleChange} required></input>

                <label >Message: </label>
                <textarea  rows={4} name="message" value={formData.message} onChange={handleChange} required></textarea>

                <input type = "submit"></input>

            </form>
            <p id = "contactForm_note">Under construction</p>
        </div>
        
    );
    };
};


export default Contact;
